import React, { useState } from "react";
import Select from "./Select";
import Input from "./Input";
import { units } from "../units";

function App() {
  const [result, setResult] = useState(0);
  const listItems = ["Meter","Centimeter","Milimeter","Kilometer"];
  // const updatedListItems = listItems.map((item,index)=> 
  // {
  //   return <li key={index}>{item.text}</li>
  // }
  // );

  const updatedListItems = listItems.forEach((o,i)=>o.id = i+1);
  return (
    <>
      <div className="converter-form">
        {/* Input with label "Amount" here */}

        {/* <label for='Amount'>Amount</label>
        <input type='text' id="Amount"/> */}

        <Input label="Amount"/>

        <div className="row">
          {/* Selects with labels "From" and "To" here */}

          {/* <label >
            From
            <select>
              <option value="Meter">Meter</option>
              <option value="Centimeter">Centimeter</option>
              <option value="Milimeter">Milimeter</option>
              <option value="Kilometer">Kilometer</option>
            </select>
          </label> */}



          {<Select label="From" items={ updatedListItems } />}

          <label >
            To
            <select>
              <option value="Meter">Meter</option>
              <option value="Centimeter">Centimeter</option>
              <option value="Milimeter">Milimeter</option>
              <option value="Kilometer">Kilometer</option>
            </select>
          </label>

          <button>Convert</button>
        </div>
      </div>

      <div id="result">
        Result is: <span data-testid="result">{result}</span>
      </div>
    </>
  );
}

export default App;
