import React, { useState } from "react";
import Select from "./Select";
import Input from "./Input";
import { units } from "../units";

function App() {
  const [result, setResult] = useState(0);
  const [inputData,setInputData] = useState(0);

  //let inputData = <Input label="Amount" onChange={setInput}/>;
  let selectFrom = <Select label="From" items={ units } />;
  let selectTo = <Select label="To" items={units}/>;

  const convertFunc=()=>{
    //input
    //from factor
    //to factor
    setResult(inputData);
  };

  return (

    <>
      <div className="converter-form">
        {/* Input with label "Amount" here */}

        {<Input label="Amount" onChange={e => setInputData(e)}/>}

        <div className="row">
          {/* Selects with labels "From" and "To" here */}



          {selectFrom}

          {selectTo}

          <button onClick={convertFunc}>Convert</button>
        </div>
      </div>

      <div id="result">
        Result is: <span data-testid="result">{result}</span>
      </div>
    </>
  );
}

export default App;
